import React, { useRef } from 'react';
import './TreinoVisual.css'; // O CSS que criamos antes continua valendo!

// Sua lista de exercícios
const exercicios = [
    { id: 1, nome: 'Agachamento', serie: '3x 8-12, intervalo: 45"', gif: '/gifs/Agachamento com Salto.gif' },
    { id: 2, nome: 'Rosca c/ Halter', serie: '3x 8-12, intervalo: 45"', gif: '/gifs/Biceps alternado com halteres.gif' },
    { id: 3, nome: 'Agachamento c/ barra', serie: '3x 8-12, intervalo: 45"', gif: '/gifs/Agachamento livre com barra.gif' },
    { id: 4, nome: 'Supino c/ halteres', serie: '3x 8-12, intervalo: 45"', gif: '/gifs/Supino aberto com halteres no banco.gif' },
    { id: 5, nome: 'Stiff c/ barra', serie: '3x 8-12, intervalo: 45"', gif: '/gifs/Stiff com barra.gif' },
    { id: 6, nome: 'Swing Kettlebel', serie: '3x 8-12, intervalo: 45"', gif: '/gifs/Caminhada com Kettlebel.gif' }, // Ajuste o gif se tiver um específico de swing
    { id: 7, nome: 'Tríceps Testa', serie: '3x 8-12, intervalo: 45"', gif: '/gifs/Triceps testa.gif' },
    { id: 8, nome: 'Agachamento Globet', serie: '3x 8-12, intervalo: 45"', gif: '/gifs/Sumo com Kettlebel.gif' }, // Goblet é similar ao Sumô com Kettlebell
    { id: 9, nome: 'Prancha isométrica', serie: '3x 8-12, intervalo: 45"', gif: '/gifs/Ponte Frontal.gif' },
    { id: 10, nome: 'Desenvolvimento', serie: '3x 8-12, intervalo: 45"', gif: '/gifs/Desenvolvimento Unilateral.gif' },
    { id: 11, nome: 'Passada', serie: '3x 8-12, intervalo: 45"', gif: '/gifs/Avanço alternado.gif' },
    { id: 12, nome: 'Elevação Pélvica', serie: '3x 8-12, intervalo: 45"', gif: '/gifs/Elevação pelvica no banco.gif' },
];

// Função auxiliar para converter um arquivo (como um GIF) para Base64
const toBase64 = file => new Promise((resolve, reject) => {
    const reader = new FileReader();
    reader.readAsDataURL(file);
    reader.onload = () => resolve(reader.result);
    reader.onerror = error => reject(error);
});

const TreinoVisual = () => {
    const treinoRef = useRef();

    const handleExportHTML = async () => {
        const treinoElement = treinoRef.current;
        if (!treinoElement) return;

        // 1. Clona o elemento para não modificar o que está na tela
        const clone = treinoElement.cloneNode(true);

        // 2. Encontra todas as imagens (GIFs) dentro do elemento clonado
        const images = clone.querySelectorAll('img');

        // 3. Converte cada GIF para o formato Base64 e substitui o 'src'
        for (const img of images) {
            const src = img.getAttribute('src');
            try {
                // Pega o arquivo GIF da pasta 'public'
                const response = await fetch(src);
                const blob = await response.blob();
                // Converte para Base64 e troca o link pela versão embutida
                img.src = await toBase64(blob);
            } catch (error) {
                console.error('Erro ao embutir imagem:', src, error);
            }
        }

        // 4. Pega o conteúdo CSS para embutir no arquivo
        const cssContent = Array.from(document.styleSheets)
            .map(sheet => {
                try {
                    return Array.from(sheet.cssRules)
                        .map(rule => rule.cssText)
                        .join('');
                } catch (e) {
                    // Ignora erros de CSS de outros domínios
                    return '';
                }
            })
            .join('');

        // 5. Monta o documento HTML final como um texto gigante
        const htmlString = `
            <!DOCTYPE html>
            <html lang="pt-BR">
            <head>
                <meta charset="UTF-8">
                <meta name="viewport" content="width=device-width, initial-scale=1.0">
                <title>Plano de Treino</title>
                <style>
                    /* Embutindo todo o CSS necessário aqui */
                    ${cssContent}
                </style>
            </head>
            <body>
                ${clone.outerHTML}
            </body>
            </html>
        `;

        // 6. Cria um "Blob" (um objeto de arquivo) e gera o link para download
        const blob = new Blob([htmlString], { type: 'text/html' });
        const url = URL.createObjectURL(blob);

        // 7. Cria um link temporário e "clica" nele para iniciar o download
        const a = document.createElement('a');
        a.href = url;
        a.download = 'treino-animado.html'; // Nome do arquivo que será baixado
        document.body.appendChild(a);
        a.click();
        document.body.removeChild(a);
        URL.revokeObjectURL(url); // Libera a memória
    };

    return (
        <div>
            <button onClick={handleExportHTML} style={{ margin: '20px', padding: '10px 15px', fontSize: '16px' }}>
                Exportar para HTML Animado
            </button>

            {/* O conteúdo do treino que será renderizado e exportado */}
            <div ref={treinoRef} className="container-treino">
                <header className="cabecalho-treino">
                    <div>
                        <h1>TREINO A</h1>
                        <p><strong>Aquecimento:</strong> escrever aqui como será realizado</p>
                    </div>
                    <div className="logo-area">
                        SUA <br /> LOGO <br /> AQUI
                    </div>
                </header>
                <main className="grid-exercicios">
                    {exercicios.map((ex) => (
                        <div key={ex.id} className="card-exercicio">
                            <span className="numero-exercicio">{ex.id}</span>
                            <img src={ex.gif} alt={ex.nome} className="gif-exercicio" />
                            <p className="nome-exercicio"><strong>{ex.nome}</strong></p>
                            <p className="serie-exercicio">{ex.serie}</p>
                        </div>
                    ))}
                </main>
                <footer className="rodape-treino">
                    <p><strong>Volta à calma:</strong> descrever aqui como será realizado</p>
                </footer>
            </div>
        </div>
    );
};

export default TreinoVisual;
