// src/TreinoVisual.js - VERSÃO COM A CORREÇÃO DO CLIQUE

import React, { useRef } from 'react';
import './TreinoVisual.css';

const TreinoVisual = ({ lista, aquecimento, voltaCalma }) => {
    const treinoRef = useRef();

    const handleExportHTML = () => {
      alert('XEQUE-MATE! O clique no botão foi registrado com sucesso!');
      console.log('✅ O clique no botão Exportar foi registrado com sucesso no console.');
    };

    return (
        <div>
            {/* A CORREÇÃO ESTÁ AQUI: Adicionamos o 'style' com position e zIndex.
              - 'position: relative' permite que o z-index funcione.
              - 'zIndex: 100' coloca o botão em uma camada superior, na frente de qualquer elemento invisível.
            */}
            <button 
              onClick={handleExportHTML} 
              style={{ 
                margin: '20px', 
                padding: '10px 15px', 
                fontSize: '16px',
                position: 'relative', 
                zIndex: 100 
              }}
            >
                Exportar para HTML Animado
            </button>

            <div ref={treinoRef} className="container-treino">
                <header className="cabecalho-treino">
                    <div>
                        <h1>TREINO A</h1>
                        <p><strong>Aquecimento:</strong> {aquecimento || '...'}</p>
                    </div>
                    <div className="logo-area">
                        SUA <br /> LOGO <br /> AQUI
                    </div>
                </header>
                <main className="grid-exercicios">
                    {lista.map((ex, index) => (
                        <div key={index} className="card-exercicio">
                            <span className="numero-exercicio">{index + 1}</span>
                            <img src={`/gifs/${ex.gif}`} alt={ex.nome} className="gif-exercicio" />
                            <p className="nome-exercicio"><strong>{ex.nome}</strong></p>
                            <p className="serie-exercicio">3x 8-12, intervalo: 45"</p>
                        </div>
                    ))}
                </main>
                <footer className="rodape-treino">
                    <p><strong>Volta à calma:</strong> {voltaCalma || '...'}</p>
                </footer>
            </div>
        </div>
    );
};

export default TreinoVisual;
