import React, { useState, useEffect } from 'react';

function App() {
  const [busca, setBusca] = useState('');
  const [gifsPorExercicio, setGifsPorExercicio] = useState({});
  const [lista, setLista] = useState([]);

  // Carrega gifs.json ao iniciar
  useEffect(() => {
    fetch('/gifs.json')
      .then((res) => res.json())
      .then((data) => setGifsPorExercicio(data))
      .catch((err) => console.error('Erro ao carregar gifs.json:', err));
  }, []);

  // Lista de GIFs encontrados com base na busca
  const gifsEncontrados = gifsPorExercicio[busca.toLowerCase()] || [];

  // Adiciona exercício à lista lateral
  const adicionarExercicio = (gif) => {
    setLista([
      ...lista,
      {
        nome: busca.trim(),
        gif: `/gifs/${gif}`,
      },
    ]);
    setBusca('');
  };

  return (
    <div style={{ display: 'flex', padding: '2rem', gap: '2rem' }}>
      {/* Lado esquerdo: busca e seleção */}
      <div style={{ flex: 1 }}>
        <h2>Buscar exercício</h2>
        <input
          type="text"
          value={busca}
          onChange={(e) => setBusca(e.target.value)}
          placeholder="Digite o nome do exercício"
          style={{ width: '100%', padding: '0.5rem', fontSize: '1rem' }}
        />
        <div style={{ marginTop: '1rem' }}>
          {gifsEncontrados.map((gif, i) => (
            <img
              key={i}
              src={`/gifs/${gif}`}
              alt={gif}
              width="200"
              style={{ cursor: 'pointer', margin: '0.5rem', borderRadius: '8px' }}
              onClick={() => adicionarExercicio(gif)}
            />
          ))}
        </div>
      </div>

      {/* Lado direito: lista de exercícios selecionados */}
      <div style={{ flex: 1 }}>
        <h2>Treino selecionado</h2>
        <ul style={{ listStyle: 'none', padding: 0 }}>
          {lista.map((item, index) => (
            <li
              key={index}
              style={{
                fontSize: '1.2rem',
                marginBottom: '0.75rem',
                padding: '0.5rem',
                background: '#f0f0f0',
                borderRadius: '6px',
              }}
            >
              {item.nome}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
