import React from 'react';

const TreinoVisual = ({ lista, aquecimento, voltaCalma }) => {
  return (
    <div style={{ padding: '2rem', fontFamily: 'sans-serif', background: '#fff' }}>
      <h1 style={{ textAlign: 'center' }}>TREINO A</h1>
      <ol style={{ paddingLeft: '1.5rem' }}>
        {lista.map((item, index) => (
          <li key={index} style={{ marginBottom: '2rem' }}>
            <h2>{item.nome}</h2>
            <img
              src={`/gifs/${item.gif}`}
              alt={item.nome}
              style={{ width: '300px', borderRadius: '8px' }}
              loop
              autoPlay
            />
            <p style={{ fontSize: '1rem', marginTop: '0.5rem' }}>
              Série: 3x 8-12 &nbsp; | &nbsp; Intervalo: 45"
            </p>
          </li>
        ))}
      </ol>
      <div style={{ marginTop: '3rem' }}>
        <h3>Aquecimento</h3>
        <p>{aquecimento || 'Escrever aqui como será realizado'}</p>
        <h3>Volta à calma</h3>
        <p>{voltaCalma || 'Escrever aqui como será realizado'}</p>
      </div>
    </div>
  );
};

export default TreinoVisual;
