import React from 'react';
import * as XLSX from 'xlsx';
import { saveAs } from 'file-saver';

function App() {
  const dadosTreino = [
    { Exerc√≠cio: 'Agachamento', S√©ries: 4, Repeti√ß√µes: 12 },
    { Exerc√≠cio: 'Supino', S√©ries: 3, Repeti√ß√µes: 10 },
    { Exerc√≠cio: 'Remada', S√©ries: 3, Repeti√ß√µes: 10 },
  ];

  const gerarPlanilha = () => {
    const ws = XLSX.utils.json_to_sheet(dadosTreino);
    const wb = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(wb, ws, 'Treino');
    const excelBuffer = XLSX.write(wb, { bookType: 'xlsx', type: 'array' });
    const blob = new Blob([excelBuffer], { type: 'application/octet-stream' });
    saveAs(blob, 'meu-treino.xlsx');
  };

  return (
    <div style={{ textAlign: 'center', padding: '2rem' }}>
      <h1>üèãÔ∏è Meu Treino App</h1>
      <button onClick={gerarPlanilha} style={{ padding: '1rem', fontSize: '1rem' }}>
        Gerar Planilha de Treino
      </button>
    </div>
  );
}

export default App;
