// src/App.js - COM BOT√ÉO DE TESTE DE INTEGRIDADE

import React, { useState, useEffect } from 'react';
import TreinoVisual from './TreinoVisual';

function App() {
  const [busca, setBusca] = useState('');
  const [gifsPorExercicio, setGifsPorExercicio] = useState({});
  const [lista, setLista] = useState([]);
  const [aquecimento, setAquecimento] = useState('');
  const [voltaCalma, setVoltaCalma] = useState('');
  const [mostrarVisual, setMostrarVisual] = useState(false);

  useEffect(() => {
    fetch('/gifs.json')
      .then((res) => res.json())
      .then((data) => setGifsPorExercicio(data))
      .catch((err) => console.error('Erro ao carregar gifs.json:', err));
  }, []);

  useEffect(() => {
    if (mostrarVisual) {
      console.log('‚úÖ Estado mostrarVisual est√° TRUE');
      console.log('üìã Lista atual:', lista);
    }
  }, [mostrarVisual, lista]);

  const gifsEncontrados = gifsPorExercicio[busca.toLowerCase()] || [];

  const adicionarExercicio = (gif) => {
    setLista([
      ...lista,
      {
        nome: busca.trim(),
        gif: gif,
      },
    ]);
    setBusca('');
  };

  // ----- NOSSO NOVO TESTE -----
  const testeCliqueApp = () => {
    alert('FINALMENTE! O clique no App.js funciona!');
  };
  // -----------------------------

  return (
    <div style={{ padding: '2rem', fontFamily: 'sans-serif' }}>
      <h1 style={{ textAlign: 'center' }}>Montar Treino</h1>

      {/* ----- NOSSO NOVO BOT√ÉO DE TESTE ----- */}
      <button 
        onClick={testeCliqueApp} 
        style={{ background: 'red', color: 'white', padding: '15px', fontSize: '18px', display: 'block', margin: '1rem auto' }}
      >
        TESTE DE CLIQUE DIRETO NO APP.JS
      </button>
      {/* ------------------------------------ */}

      <div style={{ display: 'flex', gap: '2rem' }}>
        <div style={{ flex: 1 }}>
          <input
            type="text"
            value={busca}
            onChange={(e) => setBusca(e.target.value)}
            placeholder="Digite o nome do exerc√≠cio"
            style={{ width: '100%', padding: '0.5rem', fontSize: '1rem' }}
          />
          <div style={{ marginTop: '1rem' }}>
            {gifsEncontrados.map((gif, i) => (
              <img
                key={i}
                src={`/gifs/${gif}`}
                alt={gif}
                width="200"
                style={{ cursor: 'pointer', margin: '0.5rem', borderRadius: '8px' }}
                onClick={() => adicionarExercicio(gif)}
              />
            ))}
          </div>
        </div>

        <div style={{ flex: 1 }}>
          <h2>Treino selecionado</h2>
          <ul style={{ listStyle: 'none', padding: 0 }}>
            {lista.map((item, index) => (
              <li key={index} style={{ fontSize: '1.1rem', marginBottom: '0.75rem', padding: '0.5rem', background: '#f0f0f0', borderRadius: '6px' }}>
                <strong>{item.nome}</strong><br />
                <span style={{ fontSize: '0.9rem', color: '#555' }}>{item.gif}</span>
              </li>
            ))}
          </ul>

          <label>Aquecimento:</label>
          <input type="text" value={aquecimento} onChange={(e) => setAquecimento(e.target.value)} style={{ width: '100%', padding: '0.5rem', marginBottom: '1rem' }} />
          <label>Volta √† calma:</label>
          <input type="text" value={voltaCalma} onChange={(e) => setVoltaCalma(e.target.value)} style={{ width: '100%', padding: '0.5rem', marginBottom: '1rem' }} />

          <button
            onClick={() => setMostrarVisual(true)}
            style={{ marginTop: '1rem', padding: '0.75rem 1.5rem', fontSize: '1rem', background: '#28a745', color: 'white', border: 'none', borderRadius: '6px', cursor: 'pointer' }}
          >
            Visualizar Treino
          </button>
        </div>
      </div>

      {mostrarVisual && (
        <div style={{ marginTop: '3rem', borderTop: '2px solid #ccc', paddingTop: '2rem' }}>
          <h2 style={{ color: 'green' }}>‚úÖ Visualiza√ß√£o ativada</h2>
          <p>Total de exerc√≠cios: {lista.length}</p>
          <TreinoVisual lista={lista} aquecimento={aquecimento} voltaCalma={voltaCalma} />
        </div>
      )}
    </div>
  );
}

export default App;
