import React, { useState, useEffect } from 'react';
import TreinoVisual from './TreinoVisual';
import './Modal.css';

// Funções de codificar/decodificar o link
const codificarDadosParaLink = (dados) => {
  const stringDeDados = JSON.stringify(dados);
  return btoa(unescape(encodeURIComponent(stringDeDados)));
};
const decodificarDadosDoLink = () => {
  const params = new URLSearchParams(window.location.search);
  const data = params.get('treino');
  if (data) {
    try {
      const dadosDecodificados = decodeURIComponent(escape(atob(data)));
      return JSON.parse(dadosDecodificados);
    } catch (e) { console.error("Erro ao decodificar dados.", e); return null; }
  }
  return null;
};

// O CÉREBRO DA IA (SIMULADO - VERSÃO ESTÁVEL)
const PainelIA = ({ gifsDisponiveis, onTreinoGerado }) => {
  const [objetivo, setObjetivo] = useState('hipertrofia');
  const [nivel, setNivel] = useState('iniciante');
  const [numExercicios, setNumExercicios] = useState(3);
  const [isLoading, setIsLoading] = useState(false);

  const gerarTreinoAutomatico = () => {
    setIsLoading(true);
    setTimeout(() => {
      const exerciciosAleatorios = [...gifsDisponiveis].sort(() => 0.5 - Math.random());
      const treinoSelecionado = exerciciosAleatorios.slice(0, numExercicios);

      const treinoFormatado = treinoSelecionado.map(gifFilename => ({
        nome: gifFilename.replace('.gif', ''),
        gif: gifFilename,
      }));

      onTreinoGerado(treinoFormatado);
      setIsLoading(false);
    }, 1500);
  };

  return (
    <div style={{ background: '#e9f5ff', padding: '1.5rem', borderRadius: '8px', marginBottom: '2rem' }}>
      <h2 style={{ textAlign: 'center', margin: '0 0 1rem 0' }}>🤖 Assistente de Treino IA</h2>
      <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '1rem' }}>
        <div>
          <label>Objetivo:</label>
          <select value={objetivo} onChange={e => setObjetivo(e.target.value)} style={{ width: '100%', padding: '0.5rem' }}>
            <option value="hipertrofia">Hipertrofia</option>
            <option value="emagrecimento">Emagrecimento</option>
            <option value="resistencia">Resistência</option>
          </select>
        </div>
        <div>
          <label>Nível do Aluno:</label>
          <select value={nivel} onChange={e => setNivel(e.target.value)} style={{ width: '100%', padding: '0.5rem' }}>
            <option value="iniciante">Iniciante</option>
            <option value="intermediario">Intermediário</option>
            <option value="avancado">Avançado</option>
          </select>
        </div>
      </div>
      <div style={{ marginTop: '1rem' }}>
        <label>Número de Exercícios (1-9):</label>
        <input type="number" min="1" max="9" value={numExercicios} onChange={e => setNumExercicios(parseInt(e.target.value))} style={{ width: '100%', padding: '0.5rem' }}/>
      </div>
      <button onClick={gerarTreinoAutomatico} disabled={isLoading} style={{ width: '100%', padding: '0.75rem', marginTop: '1.5rem', background: '#007bff', color: 'white', border: 'none', borderRadius: '6px', fontSize: '1rem', cursor: 'pointer' }}>
        {isLoading ? 'Gerando treino...' : 'Gerar Treino Automático'}
      </button>
    </div>
  );
};


function App() {
  const dadosIniciais = decodificarDadosDoLink();
  const [isModoVisualizacao, setIsModoVisualizacao] = useState(!!dadosIniciais);

  const [busca, setBusca] = useState('');
  const [gifsPorExercicio, setGifsPorExercicio] = useState({});
  const [lista, setLista] = useState(dadosIniciais?.lista || []);
  const [alunoNome, setAlunoNome] = useState(dadosIniciais?.alunoNome || '');
  const [observacoes, setObservacoes] = useState(dadosIniciais?.observacoes || '');
  const [mostrarModal, setMostrarModal] = useState(false);

  useEffect(() => {
    if (!isModoVisualizacao) {
      fetch('/gifs.json')
        .then((res) => res.json())
        .then((data) => {
          const todosOsGifs = Object.values(data).flat();
          setGifsPorExercicio({ ...data, todos: todosOsGifs });
        })
        .catch((err) => console.error('Erro ao carregar gifs.json:', err));
    }
  }, [isModoVisualizacao]);

  const gifsEncontrados = gifsPorExercicio[busca.toLowerCase()] || [];

  const adicionarExercicio = (gifFilename) => {
    if (lista.length >= 9) { alert('Máximo de 9 exercícios.'); return; }
    const nomeDoExercicio = gifFilename.replace('.gif', ''); 
    setLista([ ...lista, { nome: nomeDoExercicio, gif: gifFilename, }, ]);
    setBusca('');
  };

  if (isModoVisualizacao) {
    return (
        <div style={{ padding: '1rem' }}>
          <TreinoVisual 
            lista={lista} 
            alunoNome={alunoNome} 
            observacoes={observacoes} 
            isModoVisualizacao={true}
            codificarDados={codificarDadosParaLink}
          />
        </div>
      );
  }

  return (
    <div style={{ padding: '2rem', fontFamily: 'sans-serif' }}>
      <h1 style={{ textAlign: 'center' }}>Montar Treino</h1>

      <PainelIA 
        gifsDisponiveis={gifsPorExercicio.todos || []}
        onTreinoGerado={(treinoPronto) => setLista(treinoPronto)}
      />

      <div style={{ display: 'flex', gap: '2rem', flexWrap: 'wrap' }}>
        <div style={{ flex: 1, minWidth: '300px' }}>
          <h3>Ou adicione manually:</h3>
          <input type="text" value={busca} onChange={(e) => setBusca(e.target.value)} placeholder="Digite para buscar um exercício..." style={{ width: '100%', padding: '0.5rem', fontSize: '1rem' }} />
          <div style={{ marginTop: '1rem' }}>
            {gifsEncontrados.map((gif, i) => (
              <img key={i} src={`/gifs/${gif}`} alt={gif} width="200" style={{ cursor: 'pointer', margin: '0.5rem', borderRadius: '8px' }} onClick={() => adicionarExercicio(gif)} />
            ))}
          </div>
        </div>
        <div style={{ flex: 1, minWidth: '300px' }}>
          <h2>Dados do Treino</h2>
          <label>Nome do Aluno(a):</label>
          <input type="text" value={alunoNome} onChange={(e) => setAlunoNome(e.target.value)} style={{ width: '100%', padding: '0.5rem', marginBottom: '1rem' }} />
          <label>Observações:</label>
          <textarea value={observacoes} onChange={(e) => setObservacoes(e.target.value)} style={{ width: '100%', minHeight: '80px', padding: '0.5rem', marginBottom: '1rem' }} />
          <h3>Exercícios Adicionados ({lista.length} / 9)</h3>
          <ul style={{ listStyle: 'none', padding: 0 }}>
            {lista.map((item, index) => (
              <li key={index} style={{ fontSize: '1.1rem', marginBottom: '0.75rem', padding: '0.5rem', background: '#f0f0f0', borderRadius: '6px' }}>
                <strong>{item.nome}</strong>
              </li>
            ))}
          </ul>
          <button onClick={() => setMostrarModal(true)} style={{ marginTop: '1rem', padding: '0.75rem 1.5rem', fontSize: '1rem', background: '#28a745', color: 'white', border: 'none', borderRadius: '6px', cursor: 'pointer' }}>
            Visualizar e Gerar Link
          </button>
        </div>
      </div>
      {mostrarModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <TreinoVisual 
              lista={lista} 
              alunoNome={alunoNome}
              observacoes={observacoes}
              onClose={() => setMostrarModal(false)}
              codificarDados={codificarDadosParaLink}
            />
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
