import React, { useState, useEffect } from 'react';
import TreinoVisual from './TreinoVisual';
import './Modal.css';

// ... (as funções de codificar/decodificar o link continuam as mesmas) ...
const codificarDadosParaLink = (dados) => { /* ... */ };
const decodificarDadosDoLink = () => { /* ... */ };


// ----- O CÉREBRO REAL DA IA -----
const PainelIA = ({ gifsDisponiveis, onTreinoGerado }) => {
  const [objetivo, setObjetivo] = useState('hipertrofia');
  const [nivel, setNivel] = useState('iniciante');
  const [numExercicios, setNumExercicios] = useState(3);
  const [isLoading, setIsLoading] = useState(false);
  const [apiKey, setApiKey] = useState(''); // Estado para guardar a API Key

  const gerarTreinoAutomatico = async () => {
    if (!apiKey) {
      alert('Por favor, insira sua Chave de API do Google AI para usar o assistente.');
      return;
    }

    setIsLoading(true);

    const nomesDosExercicios = gifsDisponiveis.map(g => g.replace('.gif', '')).join(', ');

    // A instrução (prompt) que será enviada para a IA
    const prompt = `
      Aja como um personal trainer especialista em montagem de treinos.
      Sua tarefa é criar um treino de ${objetivo} para um aluno de nível ${nivel}.
      O treino deve conter exatamente ${numExercicios} exercícios.
      Você DEVE OBRIGATORIAMENTE escolher os exercícios da seguinte lista: ${nomesDosExercicios}.
      Sua resposta deve ser APENAS uma lista dos nomes dos exercícios escolhidos, separados por vírgula.
      Exemplo de resposta: Agachamento com Salto,Flexão de braço,Ponte Frontal
    `;

    try {
      const response = await fetch(`https://generativelanguage.googleapis.com/v1beta/models/gemini-pro:generateContent?key=${apiKey}`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ contents: [{ parts: [{ text: prompt }] }] })
      });

      if (!response.ok) {
        throw new Error('A resposta da rede não foi OK. Verifique sua API Key.');
      }

      const data = await response.json();
      const respostaIA = data.candidates[0].content.parts[0].text.trim();
      const exerciciosSugeridos = respostaIA.split(',').map(name => name.trim());

      const treinoFormatado = exerciciosSugeridos.map(nome => ({
        nome: nome,
        gif: `${nome}.gif`,
      }));

      onTreinoGerado(treinoFormatado);

    } catch (error) {
      console.error("Erro ao chamar a IA:", error);
      alert("Houve um erro ao gerar o treino. Verifique o console para mais detalhes.");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div style={{ background: '#e9f5ff', padding: '1.5rem', borderRadius: '8px', marginBottom: '2rem' }}>
      <h2 style={{ textAlign: 'center', margin: '0 0 1rem 0' }}>🤖 Assistente de Treino IA</h2>
      {/* Campo para inserir a API Key */}
      <div style={{ marginBottom: '1rem' }}>
        <label>Sua API Key do Google AI:</label>
        <input 
          type="password" 
          value={apiKey}
          onChange={e => setApiKey(e.target.value)}
          placeholder="Cole sua chave aqui"
          style={{ width: '100%', padding: '0.5rem' }}
        />
        <a href="https://makersuite.google.com/app/apikey" target="_blank" rel="noopener noreferrer" style={{ fontSize: '0.8rem' }}>
          Não tem uma chave? Obtenha uma aqui.
        </a>
      </div>
      {/* O resto do painel... */}
      <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '1rem' }}>
        <div>
          <label>Objetivo:</label>
          <select value={objetivo} onChange={e => setObjetivo(e.target.value)} style={{ width: '100%', padding: '0.5rem' }}>
            <option value="hipertrofia">Hipertrofia</option>
            <option value="emagrecimento">Emagrecimento</option>
            <option value="resistencia">Resistência</option>
          </select>
        </div>
        <div>
          <label>Nível do Aluno:</label>
          <select value={nivel} onChange={e => setNivel(e.target.value)} style={{ width: '100%', padding: '0.5rem' }}>
            <option value="iniciante">Iniciante</option>
            <option value="intermediario">Intermediário</option>
            <option value="avancado">Avançado</option>
          </select>
        </div>
      </div>
      <div style={{ marginTop: '1rem' }}>
        <label>Número de Exercícios (1-9):</label>
        <input type="number" min="1" max="9" value={numExercicios} onChange={e => setNumExercicios(parseInt(e.target.value))} style={{ width: '100%', padding: '0.5rem' }}/>
      </div>
      <button onClick={gerarTreinoAutomatico} disabled={isLoading} style={{ width: '100%', padding: '0.75rem', marginTop: '1.5rem', background: '#007bff', color: 'white', border: 'none', borderRadius: '6px', fontSize: '1rem', cursor: 'pointer' }}>
        {isLoading ? 'Gerando treino...' : 'Gerar Treino Automático'}
      </button>
    </div>
  );
};


function App() {
  // ... (o resto do App.js continua igual) ...
}

export default App;
