import React, { useState, useEffect } from 'react';

function App() {
  const [busca, setBusca] = useState('');
  const [gifsPorExercicio, setGifsPorExercicio] = useState({});
  const [lista, setLista] = useState([]);

  useEffect(() => {
    fetch('/gifs.json')
      .then((res) => res.json())
      .then((data) => setGifsPorExercicio(data))
      .catch((err) => console.error('Erro ao carregar gifs.json:', err));
  }, []);

  const gifsEncontrados = gifsPorExercicio[busca.toLowerCase()] || [];

  const adicionarExercicio = (gif) => {
    setLista([
      ...lista,
      {
        nome: busca,
        gif: `/gifs/${gif}`,
        serie: '3x10',
        observacao: '',
      },
    ]);
    setBusca('');
  };

  const atualizarSerie = (index, novaSerie) => {
    const novaLista = [...lista];
    novaLista[index].serie = novaSerie;
    setLista(novaLista);
  };

  const atualizarObservacao = (index, texto) => {
    const novaLista = [...lista];
    novaLista[index].observacao = texto;
    setLista(novaLista);
  };

  return (
    <div style={{ display: 'flex', padding: '2rem', gap: '2rem' }}>
      {/* Lado esquerdo */}
      <div style={{ flex: 1 }}>
        <h2>Buscar exercício</h2>
        <input
          type="text"
          value={busca}
          onChange={(e) => setBusca(e.target.value)}
          placeholder="Digite o nome do exercício"
          style={{ width: '100%', padding: '0.5rem' }}
        />
        <div style={{ marginTop: '1rem' }}>
          {gifsEncontrados.map((gif, i) => (
            <img
              key={i}
              src={`/gifs/${gif}`}
              alt="gif"
              width="200"
              style={{ cursor: 'pointer', margin: '0.5rem' }}
              onClick={() => adicionarExercicio(gif)}
            />
          ))}
        </div>
      </div>

      {/* Lado direito */}
      <div style={{ flex: 1 }}>
        <h2>Treino selecionado</h2>
        {lista.map((item, index) => (
          <div key={index} style={{ marginBottom: '2rem' }}>
            <h3>{item.nome}</h3>
            <img src={item.gif} alt="gif" width="200" />
            <div>
              <label>Série: </label>
              <select
                value={item.serie}
                onChange={(e) => atualizarSerie(index, e.target.value)}
              >
                <option value="3x10">3x10</option>
                <option value="3x12">3x12</option>
                <option value="3x15">3x15</option>
              </select>
            </div>
            <div>
              <label>Observações:</label>
              <textarea
                value={item.observacao}
                onChange={(e) => atualizarObservacao(index, e.target.value)}
                rows={3}
                style={{ width: '100%' }}
              />
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
