import React, { useState, useEffect } from 'react';
import * as XLSX from 'xlsx';
import { saveAs } from 'file-saver';
import TreinoVisual from './TreinoVisual';

function App() {
  const [busca, setBusca] = useState('');
  const [gifsPorExercicio, setGifsPorExercicio] = useState({});
  const [lista, setLista] = useState([]);
  const [aquecimento, setAquecimento] = useState('');
  const [voltaCalma, setVoltaCalma] = useState('');
  const [mostrarVisual, setMostrarVisual] = useState(false);

  // Carrega gifs.json ao iniciar
  useEffect(() => {
    fetch('/gifs.json')
      .then((res) => res.json())
      .then((data) => setGifsPorExercicio(data))
      .catch((err) => console.error('Erro ao carregar gifs.json:', err));
  }, []);

  // TESTE AUTOMATIZADO: Verifica se o botão ativa o componente
  useEffect(() => {
    if (mostrarVisual) {
      console.log('✅ Estado mostrarVisual está TRUE');
      if (lista.length > 0) {
        console.log('✅ Lista de exercícios tem conteúdo');
      } else {
        console.warn('⚠️ Lista de exercícios está vazia');
      }
    }
  }, [mostrarVisual, lista]);

  // Lista de GIFs encontrados com base na busca
  const gifsEncontrados = gifsPorExercicio[busca.toLowerCase()] || [];

  // Adiciona exercício à lista lateral
  const adicionarExercicio = (gif) => {
    setLista([
      ...lista,
      {
        nome: busca.trim(),
        gif: gif,
      },
    ]);
    setBusca('');
  };

  // Exporta a planilha com links dos GIFs
  const exportarPlanilha = () => {
    const dados = lista.map((item, index) => ({
      Nº: index + 1,
      Exercício: item.nome,
      Série: '3x 8-12',
      Intervalo: '45"',
      GIF: `https://seusite.com/gifs/${item.gif}`,
    }));

    dados.push(
      {},
      { Exercício: 'Aquecimento', Série: aquecimento || 'escrever aqui como será realizado' },
      { Exercício: 'Volta à calma', Série: voltaCalma || 'escrever aqui como será realizado' }
    );

    const ws = XLSX.utils.json_to_sheet(dados);
    const wb = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(wb, ws, 'Treino A');
    const buffer = XLSX.write(wb, { bookType: 'xlsx', type: 'array' });
    const blob = new Blob([buffer], { type: 'application/octet-stream' });
    saveAs(blob, 'treino-A.xlsx');
  };

  return (
    <div style={{ padding: '2rem', fontFamily: 'sans-serif' }}>
      <h1 style={{ textAlign: 'center' }}>Montar Treino</h1>

      {/* Busca e seleção */}
      <div style={{ display: 'flex', gap: '2rem' }}>
        <div style={{ flex: 1 }}>
          <input
            type="text"
            value={busca}
            onChange={(e) => setBusca(e.target.value)}
            placeholder="Digite o nome do exercício"
            style={{ width: '100%', padding: '0.5rem', fontSize: '1rem' }}
          />
          <div style={{ marginTop: '1rem' }}>
            {gifsEncontrados.map((gif, i) => (
              <img
                key={i}
                src={`/gifs/${gif}`}
                alt={gif}
                width="200"
                style={{ cursor: 'pointer', margin: '0.5rem', borderRadius: '8px' }}
                onClick={() => adicionarExercicio(gif)}
              />
            ))}
          </div>
        </div>

        {/* Lista lateral */}
        <div style={{ flex: 1 }}>
          <h2>Treino selecionado</h2>
          <ul style={{ listStyle: 'none', padding: 0 }}>
            {lista.map((item, index) => (
              <li
                key={index}
                style={{
                  fontSize: '1.1rem',
                  marginBottom: '0.75rem',
                  padding: '0.5rem',
                  background: '#f0f0f0',
                  borderRadius: '6px',
                }}
              >
                <strong>{item.nome}</strong><br />
                <span style={{ fontSize: '0.9rem', color: '#555' }}>{item.gif}</span>
              </li>
            ))}
          </ul>

          {/* Campos extras */}
          <label>Aquecimento:</label>
          <input
            type="text"
            value={aquecimento}
            onChange={(e) => setAquecimento(e.target.value)}
            style={{ width: '100%', padding: '0.5rem', marginBottom: '1rem' }}
          />
          <label>Volta à calma:</label>
          <input
            type="text"
            value={voltaCalma}
            onChange={(e) => setVoltaCalma(e.target.value)}
            style={{ width: '100%', padding: '0.5rem', marginBottom: '1rem' }}
          />

          {/* Botões */}
          <button
            onClick={exportarPlanilha}
            style={{
              marginTop: '1rem',
              padding: '0.75rem 1.5rem',
              fontSize: '1rem',
              background: '#007bff',
              color: 'white',
              border: 'none',
              borderRadius: '6px',
              cursor: 'pointer',
              marginRight: '1rem',
            }}
          >
            Exportar Planilha
          </button>

          <button
            onClick={() => setMostrarVisual(true)}
            style={{
              marginTop: '1rem',
              padding: '0.75rem 1.5rem',
              fontSize: '1rem',
              background: '#28a745',
              color: 'white',
              border: 'none',
              borderRadius: '6px',
              cursor: 'pointer',
            }}
          >
            Visualizar Treino
          </button>
        </div>
      </div>

      {/* Visual estilo "TREINO A" */}
      {mostrarVisual && (
        <div style={{ marginTop: '3rem', borderTop: '2px solid #ccc', paddingTop: '2rem' }}>
          <TreinoVisual lista={lista} aquecimento={aquecimento} voltaCalma={voltaCalma} />
        </div>
      )}
    </div>
  );
}

export default App;
