// src/TreinoVisual.js - VERSÃO DETETIVE

import React, { useRef } from 'react';
import './TreinoVisual.css';

const TreinoVisual = ({ lista, aquecimento, voltaCalma }) => {
    const treinoRef = useRef();

    const toBase64 = file => new Promise((resolve, reject) => {
        const reader = new FileReader();
        reader.readAsDataURL(file);
        reader.onload = () => resolve(reader.result);
        reader.onerror = error => reject(error);
    });

    const handleExportHTML = async () => {
        // --- CHECKPOINTS DE DEBUG ---
        console.log('--- 🕵️‍♂️ INICIANDO PROCESSO DE EXPORTAÇÃO ---');

        if (lista.length === 0) {
            alert("Por favor, adicione pelo menos um exercício ao treino antes de exportar.");
            console.warn('Exportação cancelada: lista vazia.');
            return;
        }

        const treinoElement = treinoRef.current;
        if (!treinoElement) {
            console.error('ERRO CRÍTICO: Não foi possível encontrar o elemento do treino na página.');
            return;
        }

        console.log('[PASSO 1/5] ✅ Elemento do treino capturado com sucesso.');

        const clone = treinoElement.cloneNode(true);
        const images = clone.querySelectorAll('img');

        console.log(`[PASSO 2/5] 🔎 Encontradas ${images.length} imagens (GIFs) para processar.`);

        if (images.length === 0) {
            console.warn('AVISO: Nenhum GIF foi encontrado para embutir no arquivo.');
        }

        for (const [index, img] of Array.from(images).entries()) {
            const src = img.getAttribute('src');
            console.log(`  [Imagem ${index + 1}/${images.length}] ⏳ Tentando buscar o arquivo: ${src}`);
            try {
                const response = await fetch(src);
                if (!response.ok) {
                   // Se o arquivo não for encontrado (erro 404), este erro aparecerá
                   console.error(`  ❌ FALHA ao buscar ${src}. Status da resposta: ${response.statusText}`);
                   continue; // Pula para a próxima imagem
                }
                const blob = await response.blob();
                img.src = await toBase64(blob);
                console.log(`  [Imagem ${index + 1}] ✅ SUCESSO ao embutir o GIF.`);
            } catch (error) {
                console.error(`  ❌ ERRO INESPERADO ao processar a imagem ${src}:`, error);
            }
        }

        console.log('[PASSO 3/5] ✅ Processamento de todas as imagens finalizado.');

        const cssContent = Array.from(document.styleSheets).map(sheet => {
            try {
                return Array.from(sheet.cssRules).map(rule => rule.cssText).join('');
            } catch (e) { return ''; }
        }).join('');

        console.log('[PASSO 4/5] 📄 Montando o arquivo HTML final...');

        const htmlString = `
            <!DOCTYPE html><html lang="pt-BR"><head><meta charset="UTF-8"><meta name="viewport" content="width=device-width, initial-scale=1.0"><title>Plano de Treino</title><style>${cssContent}</style></head><body>${clone.outerHTML}</body></html>`;

        const blob = new Blob([htmlString], { type: 'text/html' });
        const url = URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = 'treino-animado.html';
        document.body.appendChild(a);

        console.log('[PASSO 5/5] 🚀 Disparando o download do arquivo...');
        a.click();

        document.body.removeChild(a);
        URL.revokeObjectURL(url);

        console.log('--- ✅ PROCESSO FINALIZADO ---');
    };

    return (
        <div>
            <button onClick={handleExportHTML} style={{ margin: '20px', padding: '10px 15px', fontSize: '16px' }}>
                Exportar para HTML Animado
            </button>
            <div ref={treinoRef} className="container-treino">
                <header className="cabecalho-treino">
                    <div><h1>TREINO A</h1><p><strong>Aquecimento:</strong> {aquecimento || '...'}</p></div>
                    <div className="logo-area">SUA <br /> LOGO <br /> AQUI</div>
                </header>
                <main className="grid-exercicios">
                    {lista.map((ex, index) => (
                        <div key={index} className="card-exercicio">
                            <span className="numero-exercicio">{index + 1}</span>
                            <img src={`/gifs/${ex.gif}`} alt={ex.nome} className="gif-exercicio" />
                            <p className="nome-exercicio"><strong>{ex.nome}</strong></p>
                            <p className="serie-exercicio">3x 8-12, intervalo: 45"</p>
                        </div>
                    ))}
                </main>
                <footer className="rodape-treino">
                    <p><strong>Volta à calma:</strong> {voltaCalma || '...'}</p>
                </footer>
            </div>
        </div>
    );
};

export default TreinoVisual;
