// src/TreinoVisual.js - VERSÃO FINAL COM LOGO E LAYOUT AJUSTADO

import React, { useRef } from 'react';
import './TreinoVisual.css'; // Vamos atualizar este arquivo a seguir

// TROQUE 'logo.png' PELO NOME EXATO DO SEU ARQUIVO DE LOGO
const LOGO_FILENAME = 'logo.png'; 

const TreinoVisual = ({ lista, alunoNome, observacoes, onClose }) => {
    const treinoRef = useRef();

    const toBase64 = file => new Promise((resolve, reject) => {
        const reader = new FileReader(); reader.readAsDataURL(file);
        reader.onload = () => resolve(reader.result); reader.onerror = error => reject(error);
    });

    const handleExportHTML = async () => { /* ... (a lógica de exportação não muda) ... */
        const treinoElement = treinoRef.current; if (!treinoElement) { return; }
        const clone = treinoElement.cloneNode(true); const images = clone.querySelectorAll('img');
        for (const img of images) {
            const src = img.getAttribute('src');
            try {
                const response = await fetch(src); if (!response.ok) continue;
                const blob = await response.blob(); img.src = await toBase64(blob);
            } catch (error) { console.error(`Erro ao processar imagem ${src}:`, error); }
        }
        const cssContent = Array.from(document.styleSheets).map(sheet => {
            try { return Array.from(sheet.cssRules).map(rule => rule.cssText).join('');
            } catch (e) { return ''; }
        }).join('');
        const htmlString = `<!DOCTYPE html><html lang="pt-BR"><head><meta charset="UTF-8"><meta name="viewport" content="width=device-width, initial-scale=1.0"><title>Plano de Treino - ${alunoNome}</title><style>${cssContent}</style></head><body>${clone.outerHTML}</body></html>`;
        const blob = new Blob([htmlString], { type: 'text/html' }); const url = URL.createObjectURL(blob);
        const a = document.createElement('a'); a.href = url; a.download = `treino-${alunoNome.replace(/ /g, '_') || 'aluno'}.html`;
        document.body.appendChild(a); a.click(); document.body.removeChild(a); URL.revokeObjectURL(url);
    };

    return (
        <div>
            <div className="modal-header">
                <h2>Pré-visualização do Treino</h2>
                <button onClick={onClose} className="close-button">&times;</button>
            </div>
            <div className="modal-actions">
                <button onClick={handleExportHTML} className="export-button">Exportar HTML Animado</button>
            </div>

            <div ref={treinoRef} className="container-treino">
                <header className="cabecalho-treino-novo">
                    <div className="cabecalho-texto">
                      <h2>Rodolfo Ferraz - Personal Trainer</h2>
                      <p><strong>Aluno(a):</strong> {alunoNome || '____________________'}</p>
                    </div>
                    <div className="cabecalho-logo">
                        {/* A LOGO SERÁ INSERIDA AQUI */}
                        <img src={`/${LOGO_FILENAME}`} alt="Logo Personal Trainer" className="logo-img" />
                    </div>
                </header>

                <main className="grid-exercicios">
                    {lista.slice(0, 9).map((ex, index) => (
                        <div key={index} className="card-exercicio">
                            <span className="numero-exercicio">{index + 1}</span>
                            <img src={`/gifs/${ex.gif}`} alt={ex.nome} className="gif-exercicio" />
                            <p className="nome-exercicio"><strong>{ex.nome}</strong></p>
                            <p className="serie-exercicio">3x 8-12, intervalo: 45"</p>
                        </div>
                    ))}
                </main>

                <footer className="rodape-treino-novo">
                    <h3>Observações:</h3>
                    <p className="observacoes-texto">{observacoes || 'Nenhuma.'}</p>
                </footer>
            </div>
        </div>
    );
};
export default TreinoVisual;
