import React, { useState, useEffect } from 'react';
import TreinoVisual from './TreinoVisual';
import './Modal.css';

// ... (as fun√ß√µes de codificar/decodificar o link continuam as mesmas) ...
const codificarDadosParaLink = (dados) => { /* ... */ };
const decodificarDadosDoLink = () => { /* ... */ };


// ----- O C√âREBRO DA IA AGORA √â O DA OPENAI (GPT) -----
const PainelIA = ({ gifsDisponiveis, onTreinoGerado }) => {
  const [objetivo, setObjetivo] = useState('hipertrofia');
  const [nivel, setNivel] = useState('iniciante');
  const [numExercicios, setNumExercicios] = useState(3);
  const [isLoading, setIsLoading] = useState(false);

  // Lendo a nova chave do nosso "cofre" .env
  const apiKey = process.env.REACT_APP_OPENAI_API_KEY;

  const gerarTreinoAutomatico = async () => {
    if (!apiKey) {
      alert('ERRO DE CONFIGURA√á√ÉO: A Chave de API da OpenAI n√£o foi encontrada. Verifique seu arquivo .env e reinicie o servidor.');
      return;
    }

    setIsLoading(true);
    const nomesDosExercicios = gifsDisponiveis.map(g => g.replace('.gif', '')).join(', ');

    // O prompt √© o mesmo, a IA da OpenAI tamb√©m entender√°
    const prompt = `
      Aja como um personal trainer especialista em montagem de treinos.
      Sua tarefa √© criar um treino de ${objetivo} para um aluno de n√≠vel ${nivel}.
      O treino deve conter exatamente ${numExercicios} exerc√≠cios.
      Voc√™ DEVE OBRIGATORIAMENTE escolher os exerc√≠cios da seguinte lista: ${nomesDosExercicios}.
      Sua resposta deve ser APENAS uma lista dos nomes dos exerc√≠cios escolhidos, separados por v√≠rgula, sem nenhuma outra formata√ß√£o ou texto adicional.
      Exemplo de resposta: Agachamento com Salto,Flex√£o de bra√ßo,Ponte Frontal
    `;

    try {
      // MUDAN√áA: A URL e a estrutura da chamada s√£o diferentes para a OpenAI
      const response = await fetch('https://api.openai.com/v1/chat/completions', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${apiKey}` // OpenAI usa um "Bearer Token"
        },
        body: JSON.stringify({
          model: "gpt-3.5-turbo", // O modelo mais r√°pido e econ√¥mico da OpenAI
          messages: [{ role: "user", content: prompt }],
          temperature: 0.7,
        })
      });

      if (!response.ok) {
        const errorBody = await response.json();
        throw new Error(`Erro da API da OpenAI: ${errorBody.error.message}`);
      }

      const data = await response.json();
      // MUDAN√áA: A resposta da OpenAI vem em um formato diferente
      const respostaIA = data.choices[0].message.content.trim();
      const exerciciosSugeridos = respostaIA.split(',').map(name => name.trim());

      const treinoFormatado = exerciciosSugeridos.map(nome => ({
        nome: nome,
        gif: `${nome}.gif`,
      }));

      onTreinoGerado(treinoFormatado);

    } catch (error) {
      alert(`Houve um erro ao gerar o treino com a OpenAI. Erro: ${error.message}`);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div style={{ background: '#e9f5ff', padding: '1.5rem', borderRadius: '8px', marginBottom: '2rem' }}>
      <h2 style={{ textAlign: 'center', margin: '0 0 1rem 0' }}>ü§ñ Assistente de Treino IA (OpenAI)</h2>
      {/* O resto do PainelIA n√£o muda */}
      <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '1rem' }}>
        {/* ... selects de objetivo e n√≠vel ... */}
      </div>
      <div style={{ marginTop: '1rem' }}>
        {/* ... input de n√∫mero de exerc√≠cios ... */}
      </div>
      <button onClick={gerarTreinoAutomatico} disabled={isLoading} style={{ /* ... */ }}>
        {isLoading ? 'Gerando treino...' : 'Gerar Treino Autom√°tico'}
      </button>
    </div>
  );
};


